    private void jbtresultadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtresultadoActionPerformed

        valor2 = Double.parseDouble(jfTela.getText());
        if (operacao == "soma") {
            jfTela.setText(String.valueOf(valor1 + valor2));
        }
        if (operacao == "subtracao") {
            jfTela.setText(String.valueOf(valor1 - valor2));
        }
        if (operacao == "multiplicacao") {
            jfTela.setText(String.valueOf(valor1 * valor2));
        }
        if (operacao == "divisao") {
            jfTela.setText(String.valueOf(valor1 / valor2));
        }
        if (operacao == "quadrado") {
            jfTela.setText(String.valueOf(valor1 * valor1));
        }

    }//GEN-LAST:event_jbtresultadoActionPerformed

