    private void jbtsinalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtsinalActionPerformed
        double ops = Double.parseDouble(String.valueOf(jfTela.getText()));
        ops = ops * (-1);
        jfTela.setText(String.valueOf(ops));
    }//GEN-LAST:event_jbtsinalActionPerformed

    private void jbtbackspaceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtbackspaceActionPerformed
        String backspace = null;

        if (jfTela.getText().length() > 0) {
            StringBuilder strB = new StringBuilder(jfTela.getText());
            strB.deleteCharAt(jfTela.getText().length() - 1);
            backspace = strB.toString();
            jfTela.setText(backspace);
        }
    }//GEN-LAST:event_jbtbackspaceActionPerformed

